pragma solidity ^0.8.0;

interface IStakingTokenProvider {
    function uniLikeExchange() external returns (address);

    function defaultPairedToken() external returns (address);

    function defaultPrefix() external returns (string memory);

    function pairedToken(address) external returns (address);

    function pairedPrefix(address) external returns (string memory);

    function __StakingTokenProvider_init(address _uniLikeExchange, address _defaultPairedtoken, string memory _defaultPrefix) external;

    function setPairedTokenForVaultToken(address _vaultToken, address _newPairedToken, string calldata _newPrefix) external;

    function setDefaultPairedToken(address _defaultPairedToken, string calldata _defaultPrefix) external;

    function stakingTokenForVaultToken(address _vaultToken) external view returns (address);

    function nameForStakingToken(address _vaultToken) external view returns (string memory);

    function pairForVaultToken(address _vaultToken, address _pairedToken) external view returns (address);

    event DefaultPairedTokenUpdated(address oldDefaultPairedToken, address newDefaultPairedToken);
    event PairedTokenForVaultUpdated(address vaultToken, address oldPairedtoken, address newPairedToken);

    error IdenticalAddress();
    error ZeroAddress();
}","keccak256":"0x9e1fb1537606cdea4faf2baf748657800337b12d2586f1f83cc3d96cbaec17ce","license":"MIT"},"src/contracts/interfaces/IVaultManager.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

interface IVaultManager {
    function fnftSingleFactory() external view returns (address);

    function fnftCollectionFactory() external view returns (address);

    function excludedFromFees(address) external view returns (bool);

    function feeDistributor() external view returns (address);

    function __VaultManager_init(address _weth) external;

    function WETH() external view returns (address);

    function priceOracle() external view returns (address);

    function ifoFactory() external view returns (address);

    function zapContract() external view returns (address);

    function feeReceiver() external view returns (address payable);

    function togglePaused() external;

    function addVault(address _fnft) external returns (uint256 vaultId);

    function setPriceOracle(address _priceOracle) external;

    function setFeeDistributor(address _feeDistributor) external;

    function setFeeExclusion(address _address, bool _excluded) external;

    function setFeeReceiver(address payable _feeReceiver) external;

    function setIFOFactory(address _ifoFactory) external;

    function setZapContract(address _zapContract) external;

    function setFNFTCollectionFactory(address _fnftCollectionFactory) external;

    function setFNFTSingleFactory(address _fnftCollectionFactory) external;

    function vault(uint256 vaultId) external view returns (address);

    function vaults(uint256) external view returns (address);

    function numVaults() external view returns (uint);

    event PriceOracleUpdated(address oldPriceOracle, address newPriceOracle);
    event IFOFactoryUpdated(address oldIFOFactory, address newIFOFactory);
    event FeeReceiverUpdated(address oldFeeReceiver, address newFeeReceiver);
    event ZapContractUpdated(address oldZapContract, address newZapContract);
    event FNFTCollectionFactoryUpdated(address oldFNFTCollectionFactory, address newFNFTCollectionFactory);
    event FNFTSingleFactoryUpdated(address oldFNFTSingleFactory, address newFNFTSingleFactory);
    event VaultAdded(uint256 vaultId, address vault);
    event FeeDistributorUpdated(address oldFeeDistributor, address newFeeDistributor);
    event FeeExclusionUpdated(address target, bool excluded);

    error FeeTooHigh();
    error MaxAuctionLengthOutOfBounds();
    error MaxReserveFactorTooLow();
    error MinAuctionLengthOutOfBounds();
    error MinBidIncreaseOutOfBounds();
    error MinReserveFactorTooHigh();
    error MinVotePercentageTooHigh();
    error MultiplierTooLow();
    error OnlyFactory();
    error ZeroAddress();
}
