pragma solidity 0.8.13;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

import "./interfaces/IVaultManager.sol";
import "./interfaces/IFeeDistributor.sol";

contract VaultManager is
    OwnableUpgradeable,
    PausableUpgradeable,
    IVaultManager
{
    mapping(address => bool) public override excludedFromFees;

    address[] public override vaults;

    address public override feeDistributor;

    /// @notice the address who receives auction fees
    address payable public override feeReceiver;

    address public override fnftSingleFactory;

    address public override fnftCollectionFactory;

    address public override ifoFactory;

    address public override priceOracle;

    address public override zapContract;

    address public override WETH;

    function __VaultManager_init(address _weth) external override initializer {
        __Ownable_init();
        __Pausable_init();
        WETH = _weth;
        feeReceiver = payable(msg.sender);
    }

    function addVault(address _fnft) external override returns (uint256 vaultId) {
        if (_fnft == address(0)) revert ZeroAddress();
        address _feeDistributor = feeDistributor;
        if (_feeDistributor == address(0)) revert ZeroAddress();
        if (msg.sender != fnftCollectionFactory && msg.sender != fnftSingleFactory) revert OnlyFactory();
        vaultId = vaults.length;
        vaults.push(_fnft);
        IFeeDistributor(_feeDistributor).initializeVaultReceivers(vaultId);
        emit VaultAdded(vaultId, _fnft);
    }

    function numVaults() external view override returns (uint) {
        return vaults.length;
    }

    function setFeeDistributor(address _feeDistributor) public override onlyOwner {
        if (_feeDistributor == address(0)) revert ZeroAddress();
        emit FeeDistributorUpdated(feeDistributor, _feeDistributor);
        feeDistributor = _feeDistributor;
    }

    function setFeeReceiver(address payable _feeReceiver) external override onlyOwner {
        if (_feeReceiver == address(0)) revert ZeroAddress();
        emit FeeReceiverUpdated(feeReceiver, _feeReceiver);
        feeReceiver = _feeReceiver;
    }

    function setFNFTCollectionFactory(address _fnftCollectionFactory) external override onlyOwner {
        if (_fnftCollectionFactory == address(0)) revert ZeroAddress();
        emit FNFTCollectionFactoryUpdated(fnftCollectionFactory, _fnftCollectionFactory);
        fnftCollectionFactory = _fnftCollectionFactory;
    }

    function setFNFTSingleFactory(address _fnftSingleFactory) external override onlyOwner {
        if (_fnftSingleFactory == address(0)) revert ZeroAddress();
        emit FNFTSingleFactoryUpdated(fnftSingleFactory, _fnftSingleFactory);
        fnftSingleFactory = _fnftSingleFactory;
    }

    function setIFOFactory(address _ifoFactory) external override onlyOwner {
        emit IFOFactoryUpdated(ifoFactory, _ifoFactory);
        ifoFactory = _ifoFactory;
    }

    function setPriceOracle(address _priceOracle) external override onlyOwner {
        emit PriceOracleUpdated(priceOracle, _priceOracle);
        priceOracle = _priceOracle;
    }

    function setZapContract(address _zapContract) external override onlyOwner {
        if (_zapContract == address(0)) revert ZeroAddress();
        emit ZapContractUpdated(zapContract, _zapContract);
        zapContract = _zapContract;
    }

    function togglePaused() external override onlyOwner {
        paused() ? _unpause() : _pause();
    }

    function vault(uint256 vaultId) external view override returns (address) {
        return vaults[vaultId];
    }

    function setFeeExclusion(address _address, bool _excluded) public override onlyOwner {
        emit FeeExclusionUpdated(_address, _excluded);
        excludedFromFees[_address] = _excluded;
    }
}
","keccak256":"0xb7be5f9246f0910db340b80bc08946d813d65a3abf4ae923c43349d90ed71248","license":"MIT"},"src/contracts/interfaces/IFNFTStaking.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "./IVaultManager.sol";
import "./ITimelockExcludeList.sol";


interface IFNFTStaking {
    function vaultManager() external view returns (IVaultManager);

    function inventoryLockTimeErc20() external view returns (uint256);

    function timelockExcludeList() external view returns (ITimelockExcludeList);

    function __FNFTStaking_init(address _vaultManager) external;

    function setTimelockExcludeList(address _timelockExcludeList) external;

    function setInventoryLockTimeErc20(uint256 _inventoryLockTimeErc20) external;

    function isAddressTimelockExcluded(address addr, uint256 vaultId) external returns (bool);

    function deployXTokenForVault(uint256 vaultId) external;

    function receiveRewards(uint256 vaultId, uint256 amount) external returns (bool);

    function deposit(uint256 vaultId, uint256 _amount) external;

    function timelockMintFor(uint256 vaultId, uint256 amount, address to, uint256 timelockLength) external returns (uint256);

    function withdraw(uint256 vaultId, uint256 _share) external;

    function xTokenShareValue(uint256 vaultId) external returns (uint256);

    function timelockUntil(uint256 vaultId, address who) external returns (uint256);

    function balanceOf(uint256 vaultId, address who) external returns (uint256);

    function xTokenAddr(address baseToken) external returns (address);

    function vaultXToken(uint256 vaultId) external view returns (address);

    event StakingPoolCreated(uint256 vaultId, address xToken, address baseToken);
    event FeesReceived(uint256 vaultId, uint256 amount, address xToken);
    event BaseTokenDeposited(uint256 vaultId, uint256 baseTokenAmount, uint256 amount, uint256 timelockUntil, address sender);
    event XTokenWithdrawn(uint256 vaultId, uint256 baseTokenAmount, uint256 amount, address sender);

    event InventoryLockTimeErc20Updated(uint256 oldInventoryLockTimeErc20, uint256 newInventoryLockTimeErc20);
    event TimelockExcludeListUpdated(address oldTimelockExcludeList, address newTimelockExcludeList);

    error LockTooLong();
    error NotExcludedFromFees();
    error NotZapContract();
    error XTokenNotDeployed();

}","keccak256":"0xcdb51e91728e2f01a8b9090be9012d7f058c0b4875746782707a7d8d81e2706d","license":"MIT"},"src/contracts/interfaces/IFeeDistributor.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.13;

import "./IVaultManager.sol";
import "./ILPStaking.sol";
import "./IFNFTStaking.sol";

interface IFeeDistributor {

  struct FeeReceiver {
    uint256 allocPoint;
    address receiver;
    bool isContract;
  }

  function distributionPaused() external returns (bool);

  function vaultManager() external returns (IVaultManager);

  function lpStaking() external returns (ILPStaking);

  function fnftStaking() external returns (IFNFTStaking);

  function treasury() external returns (address);

  function allocTotal() external returns (uint256);

  function feeReceivers(uint256) external returns (uint256, address, bool);

  function __FeeDistributor_init(address _vaultManager, address _lpStaking, address _treasury) external;

  function rescueTokens(address token) external;

  function distribute(uint256 vaultId) external;

  function addReceiver(uint256 _allocPoint, address _receiver, bool _isContract) external;

  function initializeVaultReceivers(uint256 _vaultId) external;

  function changeReceiverAlloc(uint256 _idx, uint256 _allocPoint) external;

  function changeReceiverAddress(uint256 _idx, address _address, bool _isContract) external;

  function removeReceiver(uint256 _receiverIdx) external;

  function setTreasuryAddress(address _treasury) external;

  function setLPStakingAddress(address _lpStaking) external;

  function setFNFTStakingAddress(address _inventoryStaking) external;

  function pauseFeeDistribution(bool _pause) external;

  event TreasuryAddressUpdated(address newTreasury);
  event LPStakingAddressUpdated(address newLPStaking);
  event FNFTStakingAddressUpdated(address newFNFTStaking);
  event DistributionPaused(bool paused);
  event FeeReceiverAdded(address receiver, uint256 allocPoint);
  event FeeReceiverAllocUpdated(address receiver, uint256 allocPoint);
  event FeeReceiverAddressUpdated(address oldReceiver, address newReceiver);
